pragma solidity >=0.7.0 <0.8.0;

contract Loyal {

    mapping (address => int256) public balances;
    int256 public offset; // now everyone has 100 tokens by default.

    mapping (address => bool) public isShop;
    address public owner;

    mapping (address => mapping (address => int256)) public requestedMoney;

    modifier onlyOwner {
        require(msg.sender == owner);
        _;
    }

    modifier onlyShop {
        require(isShop[msg.sender]);
        _;
    }

    modifier onlyCustomer {
        require(!(isShop[msg.sender]));
        _;
    }


    function grantShopRole(address user) public onlyOwner {
        isShop[user] = true;
    }

    function requestMoney(address user, int256 amount) public onlyShop {
        requestedMoney[msg.sender][user] = amount;

    }

    function spendMoney(address recipientShop, int256 amount) public onlyCustomer {
        require(amount == requestedMoney[recipientShop][msg.sender]);
        requestedMoney[recipientShop][msg.sender] = 0;
        transfer(recipientShop, amount);
    }

    event Transfer(address indexed from, address indexed to, int256 value);
    constructor() public {
        owner = msg.sender;
        offset = 100;
    }

    function giveMoney(address client, int256 amount) public onlyShop {
        require(amount > 0 && isShop[client] == false);
        balances[client]+=amount;
    }

    function transfer(address to, int256 amount) public {
        require(balances[msg.sender] + offset  >= amount && amount > 0 && to != msg.sender);
        balances[msg.sender]-=amount;
        balances[to]+=amount;
        emit Transfer(msg.sender, to, amount);
   }

    function getBalance(address addr) public view returns(int256) {
        return balances[addr] + offset;
    }

}
